import React from 'react'
import styles from "./Cuidador.module.css"


function Cuidador({ preg }) {

    const { pregunta } = useContext(contexto);
    const { dataExcel } = useContext(DataExcel);

    const selectOption = (e) => {
        console.log(e.target.name)
        console.log(e.target.id)

        const agregarID = dataExcel.findIndex((data) => data.id === parseInt(e.target.name))
        console.log(agregarID)

        const prueba =  pregunta.find((data) => data.id === parseInt(e.target.name) )

        if(agregarID === -1){

         const exportData ={
             Numero:prueba.id,
             tipo:prueba.tipo,
             categoria:prueba.categoria,
             pregunta:prueba.pregunta,
             opcion_Marcada:prueba[e.target.id]
         }   
         dataExcel.push(exportData)
        }
        else{
            dataExcel[agregarID].opcionMarcada = prueba[e.target.id];
        }
        console.log(dataExcel)
    }

    const playsound = () =>{
        new Audio(preg.audio).play();
    }

    return (

        <div className={styles.question}>
            <h1>{pregunta.id}. {pregunta.pregunta}</h1>
            <img id={styles.img} src="./imagenes/sound.png" alt="" />
            <div className={styles.opciones}>
                <div>
                    <input type="radio" name={pregunta.id} id="opcion1" onClick={selectOption} />
                    <label htmlFor="opcion1">{pregunta.opcion1}</label>
                    <img src='./imagenes/sats1.png' alt='' />
                </div>
                <div>
                    <input type="radio" name={pregunta.id} id="opcion1" onClick={selectOption}/>
                    <label htmlFor="opcion2">{pregunta.opcion2}</label>
                    <img src='./imagenes/sats2.png' alt='' />
                </div>
                <div>
                    <input type="radio" name={pregunta.id} id="opcion3" onClick={selectOption}/>
                    <label htmlFor="opcion3">{pregunta.opcion3}</label>
                    <img src='./imagenes/sats3.png' alt='' />
                </div>
                <div>
                    <input type="radio" name={pregunta.id} id="opcion4" onClick={selectOption}/>
                    <label htmlFor="opcion4">{pregunta.opcion4}</label>
                    <img src='./imagenes/sats4.png' alt='' />
                </div>
                <div>
                    <input type="radio" name={pregunta.id} id="opcion5" onClick={selectOption}/>
                    <label htmlFor="opcion5">{pregunta.opcion5}</label>
                    <img src='./imagenes/sats5.png' alt='' />
                </div>
            </div>

        </div>
    )
}

export default Cuidador
