import React, { useContext } from 'react'
import styles from "./Header.module.css"
import contexto from '../../context/Contexto'
import * as FileSaver from "file-saver"
import XLSX from "sheetjs-style"


function Header() {
  const { pregunta } = useContext(contexto)
  const fileType = 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet;charset=UTF-8';
  const FileExtension = '.xlsx';
  const filename = 'Prueba'

  const exportToExcel = async () => {
    const ws = XLSX.utils.json_to_sheet(pregunta);
    const wb = { Sheets: { 'data': ws }, SheetNames: ['data'] };
    const excelBuffer = XLSX.write(wb, { bookType: 'xlsx', types: 'array' });
    const data = new Blob([excelBuffer], { type: fileType });
    FileSaver.saveAs(data, filename + FileExtension)
  }

  return (
    <div className={styles.header}>
      ENCUESTA SATISFACCIÃ“N
      <button onClick={e => exportToExcel()}>
        Excel export
       </button>
    </div>
  )
}

export default Header
